import React from "react";
import { ContactPicker } from "../contactPicker/ContactPicker";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({
  contacts,
  title,
  setTitle,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit
}) => {
  const contactNames = useMemo(()=>{
    return contacts.map(contact => contact.name)
  },[contacts])

  return (
    <form onSubmit={handleSubmit}>
<label>
<input value={title} type="text" placeholder="Appointment Name" required onChange={(e) => setTitle(e.target.value)} />
</label>

<label>Date:</label>
<input value={date || getTodayString()} type="date" onChange={(e) => setDate(e.target.value)} />

<label>Time:</label>
<input value={time} type="time" onChange={(e) => setTime(e.target.value)} />

<ContactPicker 
value={contact}
onChange={(value) => setContact(value)} contacts={contacts}
/>

<button type="submit" >Submit</button>
    </form>
  );
};
